import React, { useEffect, useState } from "react";
import logo from "../../assets/web-logo.jpg";
import user from "../../assets/user.jpg";
import MenuItem from "./MenuItem";
import "./sidebar.css";
// added more menuItems for testing
export const menuItems = [
  // {
  //   name: "Dashboard",
  //   to: "/",
  //   iconClassName: "bi bi-speedometer2",
  // },
  {
    name: "Profile",
    to: "/profile",
    iconClassName: "bi bi-person-circle",
  },
  {
    name: "Courses",
    to: "/",
    // iconClassName: `${BiBook}`,
    iconClassName: "bi bi-book",
  },
  {
    name: "Calendar",
    to: `/calendert`,
    iconClassName: "bi bi-calendar-plus",
    // subMenus: [
    //   { name: "Courses", to: "/content/courses" },
    //   { name: "Videos", to: "/content/videos" },
    // ],
  },
  {
    name: "Doubts",
    to: `/doubts`,
    iconClassName: "bi bi-question-circle-fill",
    // subMenus: [
    //   { name: "Courses", to: "/content/courses" },
    //   { name: "Videos", to: "/content/videos" },
    // ],
  },

  {
    name: "Settings",
    to: `/settings`,
    iconClassName: "bi bi-nut",
  },
  { name: "Help", to: `/help`, iconClassName: "bi bi-telephone" },
];

const SideMenu = (props) => {
  const [inactive, setInactive] = useState(false);

  useEffect(() => {
    if (inactive) {
      removeActiveClassFromSubMenu();
    }

    props.onCollapse(inactive);
  }, [inactive]);

  const removeActiveClassFromSubMenu = () => {
    document.querySelectorAll(".sub-menu").forEach((el) => {
      el.classList.remove("active");
    });
  };

  /*just a little improvement over click function of menuItem
    Now no need to use expand state variable in MenuItem component
  */
  useEffect(() => {
    let menuItems = document.querySelectorAll(".menu-item");
    menuItems.forEach((el) => {
      el.addEventListener("click", (e) => {
        const next = el.nextElementSibling;
        removeActiveClassFromSubMenu();
        menuItems.forEach((el) => el.classList.remove("active"));
        el.classList.toggle("active");
        // console.log(next);
        if (next !== null) {
          next.classList.toggle("active");
        }
      });
    });
  }, []);

  return (
    <div className={`side-menu ${inactive ? "inactive" : ""}`}>
      <div className="top-section">
        <div className="logo">
          <img src={logo} alt="web-logo" />
        </div>
        <div onClick={() => setInactive(!inactive)} className="toggle-menu-btn">
          {inactive ? (
            <i className="bi bi-arrow-right-square-fill"></i>
          ) : (
            <i className="bi bi-arrow-left-square-fill"></i>
          )}
        </div>
      </div>

      <div className="search-controller">
        <button className="search-btn">
          <i className="bi bi-search"></i>
        </button>

        <input type="text" placeholder="search" />
      </div>

      <div className="divider"></div>

      <div className="main-menu">
        <ul>
          {menuItems.map((menuItem, index) => (
            <MenuItem
              key={index}
              name={menuItem.name}
              to={menuItem.to}
              iconClassName={menuItem.iconClassName}
              onClick={(e) => {
                if (inactive) {
                  setInactive(false);
                }
              }}
            />
          ))}
        </ul>
      </div>

      <div className="side-menu-footer">
        <div className="avatar">
          <img src={user} alt="user" />
        </div>
        <div className="user-info">
          <h5>Abhinav Singh</h5>
          <p>20bcs008@iiitdmj.ac.in</p>
          {/* <i className="bi bi-arrow-return-right"></i> */}
        </div>
      </div>
    </div>
  );
};

export default SideMenu;
