import React, { useState} from "react";
import "./App.css";
import LandingPage, {login} from "./Components/Login/MainPage";
import SideMenu, { menuItems } from "./Components/Sidebar/SideMenu";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
// import Sidebar from "./Components/Sidebar/";
function App() {
  const [loginStatus, setLoginStatus] = useState(false);
  const [inactive, setInactive] = useState(false);
  const showView = <LandingPage setLoginStatus={setLoginStatus} />;
  if(loginStatus) showView;
  return (
    <div className="App">
      {!loginStatus && <LandingPage setLoginStatus ={setLoginStatus}/>}
      {loginStatus && (
        <Router>
          <SideMenu
            onCollapse={(inactive) => {
              console.log(inactive);
              setInactive(inactive);
            }}
          />

          <div className={`contain ${inactive ? "inactive" : ""}`}>
            {/* improvememt, not recorded in video, its just looping through menuItems
          instead of hard coding all the routes */}
            {menuItems.map((menu, index) => (
              <>
                <Route key={menu.name} exact={menu.exact} path={menu.to}>
                  <h1>{menu.name}</h1>
                </Route>
                {menu.subMenus && menu.subMenus.length > 0
                  ? menu.subMenus.map((subMenu, i) => (
                      <Route key={subMenu.name} path={subMenu.to}>
                        <h1>{subMenu.name}</h1>
                      </Route>
                    ))
                  : null}
              </>
            ))}
          </div>
        </Router>
      )}
    </div>
  );
}

export default App;
