import React, { useState } from "react";
import front from "../../assets/frontPic.png";
import gmail from "../../assets/gmail.png";
import "./LoginPage.css";
// import { FaBars, FaTwitter } from "react-icons/fa";
export default function MainPage() {
  const [login, setLogin] = useState(false);
  const [message, setMessage] = useState("Already have an account?");
  const [option, setOption] = useState("LOGIN");

  const optionHandler = () => {
    if (!login) {
      setMessage("Don't you have an account?");
      setLogin(true);
      setOption("SIGN UP");
    } else {
      setMessage("Already have an account?");
      setLogin(false);
      setOption("LOGIN");
    }
  };
  let showOption = <SignUp />;
  if (login) {
    showOption = <Login />;
  }
  return (
    <div className="main-box">
      <div>
        <img src={front} alt="LandingImage" className="landingImg" />
      </div>
      <div className="signLogin">
        <div className="ask">
          <p>{message}</p>
          <button className="opt-btn" onClick={optionHandler}>
            {option}
          </button>
        </div>
        {showOption}
      </div>
    </div>
  );
}
const SignUp = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [roleStudent,setRoleStudent]=useState(true);
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <main className="form-box">
      <h2>Join Now !</h2>
      <p>Create a new account</p>
      <form className="form" onSubmit={handleSubmit}>
        <div>
          {/* <p>I'm a </p> */}
          <div className="role">
            <h4>I' m </h4>
            <button
              className={`role-btn ${roleStudent && "active-btn"}`}
              onClick={() => setRoleStudent(!roleStudent)}
            >
              Student
            </button>
            <button
              className={`role-btn ${!roleStudent && "active-btn"}`}
              onClick={() => setRoleStudent(!roleStudent)}
            >
              Teacher
            </button>
          </div>
        </div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          placeholder="Your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <a className="forgotPass" href="/">
          Forgot password?
        </a>
        <button className="opt-val-btn" type="submit">
          Login
        </button>

        <hr />
        <div className="gmail">
          <p>Sign Up with </p>
          <img className="gmail-logo" src={gmail} alt="gmail-logo" />
        </div>
      </form>
    </main>
  );
};

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <main className="form-box">
      <h2>Welcome Back !</h2>
      <p>Login your account</p>
      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          placeholder="Your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <a className="forgotPass" href="/">
          Forgot password?
        </a>
        <button className="opt-val-btn" type="submit">
          Sign Up
        </button>
        <hr />
        <div className="gmail">
          <p>Login with </p>
          <img className="gmail-logo" src={gmail} alt="gmail-logo" />
        </div>
      </form>
    </main>
  );
};
