import React, { useState} from "react";
import "./App.css";
import LandingPage from "./Components/Login/MainPage";
import SideMenu, { menuItems } from "./Components/Sidebar/SideMenu";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
// import Sidebar from "./Components/Sidebar/";
function App() {
  const [loginStatus, setLoginStatus] = useState(false);
  const InsideLogin=()=>{
    const [inactive, setInactive] = useState(false);
    return (
        <Router>
          <SideMenu
            onCollapse={(inactive) => {
              // console.log(inactive);
              setInactive(inactive);
            }}
          />

          <div className={`contain ${inactive ? "inactive" : ""}`}>
            {menuItems.map((menu, index) => (
                <Route key={menu.name} exact path={menu.to}>
                  <h1>{menu.name}</h1>
                </Route>
            ))}
          </div>
        </Router>
      )
  }
  let showView = <LandingPage setLoginStatus={setLoginStatus} />;
  if(loginStatus) showView=<InsideLogin/>;
  return (
    <div>
      {/* {showView} */}
      <Router>
        <SideMenu
          onCollapse={(inactive) => {
            // console.log(inactive);
            setInactive(inactive);
          }}
        />

        <div className={`contain ${inactive ? "inactive" : ""}`}>
          {menuItems.map((menu, index) => (
            <Route key={menu.name} exact path={menu.to}>
              <h1>{menu.name}</h1>
            </Route>
          ))}
        </div>
      </Router>
    </div>
  );
}

export default App;
