import React ,{useState} from "react";
import front from "../../assets/frontPic.png";
import "./LoginPage.css";
// import { FaBars, FaTwitter } from "react-icons/fa";
export default function MainPage() {
  const [login,setLogin]=useState(false);
  const [message,setMessage]=useState('Already have an account');
  const [option,setOption]=useState("LOGIN");

  const optionHandler=()=>{
    if(!login){
      setMessage("Don't you have an account?");
      setLogin(false);
      setOption("SIGN UP")
    }
    else{
      setMessage("Already have an account?");
       setLogin(true);
       setOption("lOGIN");
    }
  }
  let showOption=<SignUp/>;
  if (login){
    showOption=(
      <Login/>
    );
  }
  return (
    <div className="main-box">
      <div className="frontImg">
        <img src={front} alt="LandingImage" className="landingImg" />
      </div>
      <div className="signLogin">
        <div className="ask">
          <p>{message}</p>
          <button className='signup-btn' onClick={optionHandler}>{option}</button>
        </div>
        {showOption}
      </div>
    </div>
  );
}
const SignUp=()=>{
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <main className="bgc">
      <h2>Join No</h2>
      <div className="holder">
        <div className="title">
          <h2 style={{ color: "white" }}>Join now</h2>
          <h3 style={{ color: "white" }}>
            Learning Management System where Learning meets the skies and every
            one not only understand but also feels
          </h3>
        </div>
        <article>
          <form className="form" onSubmit={handleSubmit}>
            <div className="form-control">
              <label htmlFor="email">Email : </label>
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-control">
              <label htmlFor="password">Password : </label>
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit">Login</button>
            {/* <button type="submit">Sign Up</button> */}
          </form>
        </article>
      </div>
    </main>
  );
}

const Login = () => {
const [email,setEmail]=useState('');
const [password,setPassword]=useState('');
const handleSubmit=(e)=>{
    e.preventDefault();
}
  return (
    <main className="bgc">
      <h2>Welecome Back</h2>
      <div className="holder">
        <div className="title">
          <h2 style={{ color: "white" }}>Join now</h2>
          <h3 style={{ color: "white" }}>

            Learning Management System where Learning meets the skies and every
            one not only understand but also feels
          </h3>
        </div>
        <article>
          <form className="form" onSubmit={handleSubmit}>
            <div className="form-control">
              <label htmlFor="email">Email : </label>
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-control">
              <label htmlFor="password">Password : </label>
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit">Login</button>
            {/* <button type="submit">Sign Up</button> */}
          </form>
        </article>
      </div>
    </main>
  );
};
